import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
    const [loggedIn, setLoggedIn] = useState(false);
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [notes, setNotes] = useState([]);
    const [title, setTitle] = useState('');
    const [content, setContent] = useState('');

    const handleLogin = async () => {
        try {
            await axios.post('http://localhost:5000/api/login', { username, password });
            setLoggedIn(true);
        } catch (err) {
            alert('Invalid credentials');
        }
    };

    const fetchNotes = async () => {
        const res = await axios.get('http://localhost:5000/api/notes');
        setNotes(res.data);
    };

    const addNote = async () => {
        await axios.post('http://localhost:5000/api/notes', { title, content });
        setTitle('');
        setContent('');
        fetchNotes();
    };

    const deleteNote = async (id) => {
        await axios.delete(`http://localhost:5000/api/notes/${id}`);
        fetchNotes();
    };

    useEffect(() => {
        if (loggedIn) fetchNotes();
    }, [loggedIn]);

    if (!loggedIn) {
        return (
            <div style={{ padding: '20px' }}>
                <h2>Login</h2>
                <input placeholder="Username" value={username} onChange={e => setUsername(e.target.value)} /><br />
                <input type="password" placeholder="Password" value={password} onChange={e => setPassword(e.target.value)} /><br />
                <button onClick={handleLogin}>Login</button>
            </div>
        );
    }

    return (
        <div style={{ padding: '20px' }}>
            <h1>Digital Notes Keeper (Local DB)</h1>
            <input placeholder="Title" value={title} onChange={e => setTitle(e.target.value)} /><br />
            <textarea placeholder="Content" value={content} onChange={e => setContent(e.target.value)} /><br />
            <button onClick={addNote}>Add Note</button>
            <hr />
            <h2>Your Notes</h2>
            {notes.map(note => (
                <div key={note.id} style={{ border: '1px solid gray', margin: '10px 0', padding: '10px' }}>
                    <h3>{note.title}</h3>
                    <p>{note.content}</p>
                    <button onClick={() => deleteNote(note.id)}>Delete</button>
                </div>
            ))}
        </div>
    );
}

export default App;
